mapper.select:
select file
from video
where video_id=#{video_id}

select name
from avatar
where avatar_id=#{avatar_now}

select icon_id, img, sec
from icon
where video_id=#{video_id}

select ranking, member_id, point
from (select @rownum:=@rownum+1 ranking, member_id, point
	from (select @rownum := 0) r, 
		(select member_id, max(point) point 
		from play 
		where video_id=#{video_id} 
		group by member_id 
		order by point desc 
		limit 10) s) my
where member_id=#{member_id}
